import React, {useState, useEffect} from 'react';
import SearchBar from './components/SearchBar';
import EmojiList from './components/EmojiList';

const emojis = require('node-emoji')

const App = () => {

  const [responseEmoji, setResponseEmoji] = useState('')

  useEffect(
    (term) => {
      (async term => {
         const response = await emojis.search('h');
         setResponseEmoji(response.data);
       // return response.map(emoji => <div key={emoji.key}>{emoji.emoji} {emoji.key}</div>)
    })(term)
  },
  []);
console.log(responseEmoji)
  return (
    <div>
      <h1>Emoji Search</h1>
      <SearchBar onFormSubmit={null} />
      <EmojiList emojis={responseEmoji}/>
      {responseEmoji}

    </div>
  )
}
export default App
