import React, {useState, useEffect} from 'react';
import SearchBar from './components/SearchBar';
import EmojiList from './components/EmojiList';

const emojis = require('node-emoji')

const App = () => {

  const [responseEmoji, setResponseEmoji] = useState('')

  useEffect(() => {
    const onTermSubmit = term => {
     const response = emojis.search('h');
     setResponseEmoji(response);
     return response.map(emoji => <div key={emoji.key}>{emoji.emoji} {emoji.key}</div>)
    }
  },[responseEmoji]);

  return (
    <div>
      <h1>Emoji Search</h1>
      <SearchBar onFormSubmit={null} />
      <EmojiList emojis={emojis}/>
      {responseEmoji}

    </div>
  )
}
export default App
